(def img-exs-l '("jpg" "png" "gif"))
(defn fetch-to-file!
  "URLの画像をローカルに保存する"
  [url file]
  (with-open [in (io/input-stream url)
              out (io/output-stream file)]
    (io/copy in out)))

(defn img-url?
  "URLが画像の拡張子かどうか判定する"
  [url img-exs]
  (if (empty? url)
    false
    (let [bl (for [img-ex img-exs]
               (and (.contains url (str "." img-ex))))]
      (if (some true? bl)
        true
        false))))


;img-exs-l
;(= (img-url? "http://test.jpg" img-exs-l) true)
;(img-url? "http://test.jp" img-exs-l)
;(img-url? "http://test" img-exs-l)

(defn get-img-ex
  "URLにimg-exsで指定された画像の拡張子があったらその拡張子を返す"
  [url img-exs]
  (first
   (for [img-ex img-exs
         :when (.contains url img-ex)]
     img-ex)))

;; (= (get-img-ex "http://test.jpg" ["jpg"]) true)
;; (= (get-img-ex "http://test.jpg" ["png"]) nil)

(defn split-img-url
  "URLを画像の拡張子で切る
  例:
  http://test.jpg?hogehoge -> http://test.jpg"
  [url img-exs]
  (let [b (img-url? url img-exs)
        img-ex (get-img-ex url img-exs)]
    (if (true? b)
      (str (first (.split url (str "." img-ex))) (str "." img-ex))
      false)))

(defn cons-rel-img-url
  "相対パスの画像urlを補完して正しいurlを返す"
  [rel-img-url url]
  (if (.startsWith rel-img-url "http")
    rel-img-url ;httpから始まっていたらそのまま返す
    (str url rel-img-url)))

(defn cons-img-url-list-a
  "URLのリストから画像URLを摘出しリストを生成する aタグ用"
  [url url-list img-exs]
  (for [img url-list
        :when (img-url? (get-in img [:attrs :href]) img-exs)]
    (let [iurl (get-in img [:attrs :href])]
      (-> iurl
          (split-img-url img-exs)
          (cons-rel-img-url url)))))


(defn cons-img-url-list-img
  "URLのリストから画像URLを摘出しリストを生成する imgタグ用"
  [url url-list img-exs]
  (for [img url-list
        :when (img-url? (get-in img [:attrs :src]) img-exs)]
    (let [iurl (get-in img [:attrs :src])]
      (-> iurl
          (split-img-url img-exs)
          (cons-rel-img-url img-exs)))))

(defn get-file-name
  "保存用ファイル名取得"
  [file]
  (last (. file split "/")))

(defn fetch-to-files!
  "渡された複数のURLの画像をローカルに保存する"
  [urls save-f-dir]
  (for [url urls]
    (->> url
         get-file-name
         (str save-f-dir)
         (fetch-to-file! url))))

(defn cons-list
  "URLのソース内をタグで摘出したリストを生成"
  [url tag]
  (-> url
      URL.
      html-resource
      (select [tag])))

